import Link from "next/link";

const Links = ({ children }) => {
  return query && query !== "" ? (
    <Link href={{ pathname: `/` }}>{children}</Link>
  ) : (
    <Link href={{ pathname: `/` }}>{children}</Link>
  );
};

const Header = () => {
  const links = [
    { name: "movie", icon: "film" },
    { name: "serie", icon: "tv" },
    { name: "game", icon: "gamepad" },
  ];
  return (
    <ul class="nav nav-pills nav-fill" style={{ marginBottom: "20px" }}>
      {links.map((link) => {
        return (
          <li className="nav-item" onClick={() => {}}>
            <Links {...link}>
              <span
                className={`nav-link ${link.name === "Movie" && "isActive"}`}
              >
                <i className={`fas fa-${link.icon}`}></i> &nbsp; {link.name}{" "}
              </span>
            </Links>
          </li>
        );
      })}
    </ul>
  );
};
export default Header;
