import Link from "next/link";
import { Context } from "../lib/Context";

const Links = ({ children, query }) => {
  return query !== "" ? (
    <Link href={{ pathname: `/` }}>{children}</Link>
  ) : (
    <Link href={{ pathname: `/` }}>{children}</Link>
  );
};

const Header = () => {
  const { setActive, links } = React.useContext(Context);
  return (
    <ul class="nav nav-pills nav-fill" style={{ marginBottom: "20px" }}>
      {links.map((link) => {
        return (
          <li className="nav-item" onClick={() => {}}>
            <Links query="" {...link}>
              <span
                className={`nav-link ${link.name === "movie" && "isActive"}`}
              >
                <i className={`fas fa-${link.icon}`}></i> &nbsp; {link.name}{" "}
              </span>
            </Links>
          </li>
        );
      })}
    </ul>
  );
};
export default Header;
